export default {
  props: {
    text: {
      type: String,
      required: true
    },
    variant: {
      type: String,
      default: "primary",
      validator(propValue) {
        return ["primary", "secondary"].includes(propValue);
      }
    }
  },
  computed: {
    styling() {
      return ["btn", `btn--${this.variant}`];
    }
  }
};
